public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10,b=20;
		int temp;
		System.out.println("a="+a+" b="+b);	//before swapping
		
		temp = a;	//Value of temp = 3
		a = b;		// a = 5
		b = temp;	//b = 3
		System.out.println("a="+a+" b="+b);	//after swapping
		
		

	}

}
